$client = New-Object System.Net.Sockets.TcpClient; $client.Connect("poop-monitor.local", 23); $stream = $client.GetStream(); $reader = New-Object System.IO.StreamReader($stream); $writer = New-Object System.IO.StreamWriter($stream); Write-Host "Connected to ESP32!"; while($client.Connected) { if($stream.DataAvailable) { Write-Host $reader.ReadLine() } Start-Sleep -Milliseconds 100 }  